/*Table structure for table `cms_comment_photo` */

DROP TABLE IF EXISTS `cms_comment_photo`;

CREATE TABLE `cms_comment_photo` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `comment_id` int(10) unsigned NOT NULL COMMENT '评论ID',
  `images` varchar(255) NOT NULL DEFAULT '' COMMENT '图片地址',
  `is_show` tinyint(4) NOT NULL DEFAULT '1' COMMENT '是否显示',
  `sort` int(11) NOT NULL DEFAULT '99' COMMENT '排序',
  `add_time` int(11) DEFAULT NULL COMMENT '创建时间',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=4 DEFAULT CHARSET=utf8;

